import React, { useState, useEffect } from 'react';


function App() {
  const [codigo, setCodigo] = useState('');
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [nameSubmitted, setNameSubmitted] = useState(false);
  const [roleSelected, setRoleSelected] = useState('');
  const [codigoInvalido, setCodigoInvalido] = useState(false);

  const handleNameSubmit = async () => {
    if (roleSelected === 'Alumno') {
      const response = await fetch('/codigo_respuesta', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ codigo }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setNameSubmitted(true);
          setCodigoInvalido(false);
          fetchData();
        } else {
          setCodigoInvalido(true);
          console.log('Error, codigo incorrecto');
        }
      } else {
        console.log('Error en la solicitud');
      }
    }

  };

  const handleRoleSelection = async (role) => {
    setRoleSelected(role);
  };

  const fetchData = async () => {
    console.log('AA')
    const response = await fetch('/preguntas', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ codigo }),
    });
    console.log('BB')
    const data = await response.json();
    console.log('CC')
    console.log(data)
    console.log(roleSelected)
    console.log('DD')
    setData(data);
  };

  useEffect(() => {
    if (roleSelected === 'Alumno') {
      fetchData();
    }
  }, [roleSelected]);

  return (
    <div>
      {!roleSelected ? (
        <div>
          <button onClick={() => handleRoleSelection('Alumno')}>Alumno</button>
          <button onClick={() => handleRoleSelection('Anfitrión')}>Anfitrión</button>
        </div>
      ) : nameSubmitted === "Alumno" && (
        !nameSubmitted ? (
          <div>
            <input
              type="text"
              value={codigo}
              onChange={(e) => setCodigo(e.target.value)}
              placeholder="Introduce el codigo dado por el anfitrion"
            />
            <button onClick={handleNameSubmit}>Enviar</button>
            {codigoInvalido && (
              <p>Codigo incorrecto, recarga la pagina e intente nuevamente</p>
            )}
          </div>
        ) : (
          roleSelected === 'Alumno' && (
            <div>
              {data && data.length > 0 ? (
                data.map((member, i) => (
                  <p key={i}>{member}</p>
                ))
              ) : (
                <p>No hay preguntas disponibles.</p>
              )}
            </div>
          )
        )
      ) }
    </div>
  );
}


export default App;


