import React, { useState, useEffect } from 'react';

function App() {
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [roleSelected, setRoleSelected] = useState('');

  const handleRoleSelection = (role) => {
    setRoleSelected(role);
  };

  const handleNameSubmit = async () => {
    if (name.trim() !== '') {
      const response = await fetch('/home', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      if (response.ok) {
        fetchData();
      }
    }
  };

  const fetchData = async () => {
    const res = await fetch('/home');
    const data = await res.json();
    setData(data);
  };

  return (
    <div>
      {!roleSelected ? (
        <div>
          <button onClick={() => handleRoleSelection('Alumno')}>Alumno</button>
          <button onClick={() => handleRoleSelection('Anfitrión')}>Anfitrión</button>
        </div>
      ) : roleSelected === 'Alumno' && data.length === 0 ? (
        <div>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Introduce tu nombre"
          />
          <button onClick={handleNameSubmit}>Enviar Nombre</button>
        </div>
      ) : (
        roleSelected === 'Alumno' && (
          <div>
            {data.preguntas && data.preguntas.length > 0 ? (
              data.preguntas.map((member, i) => (
                <p key={i}>{member}</p>
              ))
            ) : (
              <p>No hay preguntas disponibles.</p>
            )}
          </div>
        )
      )}
    </div>
  );
}

export default App;



